package week1.day1;

public class ControlStatementsClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
int num=72;



if(num%3==0 && num%5 == 0) {
	System.out.println("TRIZZ & FRIZZ");
}

else if(num%5==0) {
	System.out.println("FRIZZ");
}

else if(num%3 == 0) {
	System.out.println("TRIZZ ");
}

	}

}
